INSERT INTO `news` VALUES (1004, '实验报告', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:隶书\">青</span></span></strong><strong> </strong><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:隶书\">岛</span></span></strong><strong> </strong><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:隶书\">理</span></span></strong><strong> </strong><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:隶书\">工</span></span></strong><strong> </strong><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:隶书\">大</span></span></strong><strong> </strong><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:隶书\">学</span></span></strong></span></span></p>\n\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:15.0pt\"><span style=\"font-family:宋体\">课程实验报告</span></span></strong></span></span></p>\n\n<table border=\"1\" cellspacing=\"0\" class=\"Table\" style=\"border-collapse:collapse; border:1pt solid windowtext; width:1000px\">\n	<tbody>\n		<tr>\n			<td style=\"height:21.6pt; width:58.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">课程名称</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:102.15pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">软件代码开发技术</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:35.2pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">班级</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:83.55pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">软件151</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:59.45pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">实验日期</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:95.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">2017.10.23</span></span></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"height:21.6pt; width:58.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">姓名</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:102.15pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">王海涛</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:35.2pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">学号</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:83.55pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">201507125</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:59.45pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">实验成绩</span></span></span></p>\n			</td>\n			<td style=\"height:21.6pt; width:95.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"height:21.6pt; width:58.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">实验名称</span></span></span></p>\n			</td>\n			<td colspan=\"5\" style=\"height:21.6pt; width:375.6pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:宋体\">数据库综合应用</span></span><span style=\"font-size:12.0pt\">_</span><span style=\"font-size:12.0pt\"><span style=\"font-family:宋体\">用户信息管理系统</span></span></span></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"height:72.0pt; width:58.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">实验目的</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">及要求</span></span></span></p>\n			</td>\n			<td colspan=\"5\" style=\"height:72.0pt; vertical-align:top; width:375.6pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\">&nbsp;</p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\">&nbsp;</p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\">&nbsp;</p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\">&nbsp;</p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"height:72.0pt; width:58.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">实验环境</span></span></span></p>\n			</td>\n			<td colspan=\"5\" style=\"height:72.0pt; width:375.6pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Windows 10 Enterprise</span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Visual Studio 2017</span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chrome 63.0.3239.84</span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">SQL Server 2016</span></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"height:36.4pt; width:58.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">实验内容</span></span></span></p>\n			</td>\n			<td colspan=\"5\" style=\"height:36.4pt; vertical-align:top; width:375.6pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.<span style=\"font-family:宋体\">建立一个解决方案</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.<span style=\"font-family:宋体\">建立增、删、改、查程序</span></span></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"height:72.85pt; width:58.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">算法描述及实验步骤</span></span></span></p>\n			</td>\n			<td colspan=\"5\" style=\"height:72.85pt; vertical-align:top; width:375.6pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.<span style=\"font-family:宋体\">建立</span>index.aspx<span style=\"font-family:宋体\">网页，设计网页布局和界面。</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.<span style=\"font-family:宋体\">通过</span>div<span style=\"font-family:宋体\">和</span>css<span style=\"font-family:宋体\">设计网页布局。</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">3.<span style=\"font-family:宋体\">编写</span>index.aspx.cs<span style=\"font-family:宋体\">后端代码，添加</span>validator<span style=\"font-family:宋体\">控件，设置验证参数和验证作用域。</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">4.<span style=\"font-family:宋体\">调用</span>SQL<span style=\"font-family:宋体\">语句，读取数据库，将数据读取到</span>ResultSet<span style=\"font-family:宋体\">中，遍历数据，将数据写入到页面。</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:left\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">5.<span style=\"font-family:宋体\">通过后端读取，用</span>Response<span style=\"font-family:宋体\">写回浏览器页面。</span></span></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td style=\"height:72.85pt; width:58.25pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">调试过程及实验结果</span></span></span></p>\n			</td>\n			<td colspan=\"5\" style=\"height:72.85pt; vertical-align:top; width:375.6pt\">\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.<span style=\"font-family:宋体\">登录界面</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">&nbsp;</p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">图</span>1.1 <span style=\"font-family:宋体\">登录界面</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.<span style=\"font-family:宋体\">数据显示界面</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">&nbsp;</p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:10.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:宋体\">图</span>1.2 <span style=\"font-family:宋体\">信息显示界面</span></span></span></p>\n\n			<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-s